import { useState, useEffect } from "react";

export default function useStats(url) {
    const [stats, setStats] = useState();
    useEffect(() => {
        console
        async function fetchData() {
            console.log('Fetching Data');
            const data = await fetch(url). then
            (res => res.json()
            ); 
            setStats(data);
        }
        fetchData();
    }, []);
    return stats;
}
